import{_ as e,c as n,o as t,N as a}from"./chunks/framework.0Wz7YRG7.js";const r="/staging/assets/18-scheduled-maintenance.Z_WyvtVq.png",i="/staging/assets/19-imminent-maintenance.FCULu9Cq.png",s="/staging/assets/20-active-maintenance.gT_KgFTw.png",_=JSON.parse('{"title":"Maintenance Mode","description":"","frontmatter":{"title":"Maintenance Mode","outline":[0,4],"order":6},"headers":[],"relativePath":"tutorials/ultra-bridge/maintenance-mode.md","filePath":"tutorials/ultra-bridge/maintenance-mode.md","lastUpdated":null}'),o={name:"tutorials/ultra-bridge/maintenance-mode.md"},l=a('<h1 id="maintenance-mode" tabindex="-1">Maintenance Mode <a class="header-anchor" href="#maintenance-mode" aria-label="Permalink to &quot;Maintenance Mode&quot;">​</a></h1><p>Understanding bridge maintenance periods and how they affect transfers.</p><p><strong>Bridge URL</strong>: <a href="https://bridge.testnet.ultra.io/" target="_blank" rel="noreferrer">https://bridge.testnet.ultra.io/</a></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Scheduled maintenance ensures bridge security and performance. <strong>During active maintenance, ALL bridge functions are unavailable</strong> - no new transfers, no claims, and no resume functions will work.</p><h2 id="maintenance-states" tabindex="-1">Maintenance States <a class="header-anchor" href="#maintenance-states" aria-label="Permalink to &quot;Maintenance States&quot;">​</a></h2><h3 id="_1-scheduled-maintenance" tabindex="-1">1. Scheduled Maintenance <a class="header-anchor" href="#_1-scheduled-maintenance" aria-label="Permalink to &quot;1. Scheduled Maintenance&quot;">​</a></h3><ul><li><strong>Indicator</strong>: Countdown timer to maintenance start</li><li><strong>Recommended</strong>: Complete pending transactions before deadline</li><li><strong>Duration</strong>: Announced in advance</li></ul><p><img src="'+r+'" alt="Scheduled Maintenance"></p><h3 id="_2-imminent-maintenance" tabindex="-1">2. Imminent Maintenance <a class="header-anchor" href="#_2-imminent-maintenance" aria-label="Permalink to &quot;2. Imminent Maintenance&quot;">​</a></h3><ul><li><strong>Indicator</strong>: Warning with short countdown (&lt;30 min)</li><li><strong>Recommended</strong>: Do NOT start new transactions</li><li><strong>Warning</strong>: Transfers started now may experience long delays and extended arrival times</li><li><strong>Status</strong>: Complete in-progress transfers quickly</li></ul><p><img src="'+i+'" alt="Imminent Maintenance"></p><h3 id="_3-active-maintenance" tabindex="-1">3. Active Maintenance <a class="header-anchor" href="#_3-active-maintenance" aria-label="Permalink to &quot;3. Active Maintenance&quot;">​</a></h3><ul><li><strong>Indicator</strong>: Clear maintenance message</li><li><strong>Recommended</strong>: Wait for maintenance completion - all bridge functions unavailable</li><li><strong>Status</strong>: All transfers, claims, and resume functions blocked</li></ul><p><img src="'+s+'" alt="Active Maintenance"></p><h2 id="impact-on-transfers" tabindex="-1">Impact on Transfers <a class="header-anchor" href="#impact-on-transfers" aria-label="Permalink to &quot;Impact on Transfers&quot;">​</a></h2><ul><li><strong>Existing transfers</strong>: Postponed until maintenance completes</li><li><strong>Ready to claim</strong>: Cannot be claimed until maintenance ends</li><li><strong>New transfers</strong>: Completely blocked</li><li><strong>Resume function</strong>: Unavailable during maintenance, returns afterward</li><li><strong>Data safety</strong>: All pending transfers preserved and resumable</li></ul><h2 id="after-maintenance" tabindex="-1">After Maintenance <a class="header-anchor" href="#after-maintenance" aria-label="Permalink to &quot;After Maintenance&quot;">​</a></h2><p><strong>When maintenance ends:</strong></p><ol><li><strong>Check resume function</strong> for pending transfers</li><li><strong>Complete any ready claims</strong></li><li><strong>Test with small amount</strong> first</li></ol><p><strong>Transfer stuck during maintenance?</strong> Use <a href="./resuming-transactions.html">Resume Function</a> - this is normal behavior and transfers will continue after maintenance.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ol><li><strong><a href="./troubleshooting.html">Troubleshooting</a></strong> - Common issues and solutions</li><li><strong><a href="./ultra-to-evm.html">Ultra→EVM Transfer</a></strong> - Complete transfer guide</li><li><strong><a href="./resuming-transactions.html">Resume Transfers</a></strong> - Continue interrupted transfers</li></ol><h2 id="need-help" tabindex="-1">Need Help? <a class="header-anchor" href="#need-help" aria-label="Permalink to &quot;Need Help?&quot;">​</a></h2><ul><li><strong><a href="./troubleshooting.html">Troubleshooting Guide</a></strong> - Common solutions</li><li><strong><a href="https://discord.com/invite/WfJCN6YbGk" target="_blank" rel="noreferrer">Ultra Discord</a></strong> - Community support</li><li><strong>Email</strong>: <a href="mailto:contact@ultra.io" target="_blank" rel="noreferrer">contact@ultra.io</a></li><li><strong>Monitor announcements</strong> for maintenance updates</li></ul>',25),c=[l];function d(m,u,g,h,f,p){return t(),n("div",null,c)}const v=e(o,[["render",d]]);export{_ as __pageData,v as default};
